mapper.select:
select ar.id,ar.article_title articleTitle,us.user_name userName, ar.article_create_date articleCreateDate,ta.tag1,ta.tag2,ta.tag3, ar.article_intro articleIntro,ar.article_picture articlePicture, co.detial detial,ar.reading_quantity readingQuantity from article ar INNER JOIN content co on ar.article_content_id=co.content_id INNER JOIN user us on ar.posted_id = us.user_id INNER JOIN tag ta on ar.article_tag_id = ta.tag_id
and ar.id=#{id}
ORDER BY ar.article_create_date desc LIMIT 5

select * from (select ar.id, ar.article_title articleTitle,us.user_name userName,ar.article_create_date articleCreateDate,ta.tag1,ta.tag2,ta.tag3,ar.article_intro articleIntro,ar.article_picture articlePicture,co.detial detial from article ar INNER JOIN content co on ar.article_content_id=co.content_id INNER JOIN user us on ar.posted_id = us.user_id INNER JOIN tag ta on ar.article_tag_id = ta.tag_id ORDER BY ar.article_create_date desc ) tg
where tg.tag1 like CONCAT(#{tags},'%') or tg.tag2 like CONCAT(#{tags},'%') or tg.tag3 like CONCAT(#{tags},'%')
LIMIT #{currentPage},#{pageSize}
